mapper.select:
select * from( select lst.buy_image, lst.buy_product, lst.buy_price, lst.buy_number, lst.BUY_QUANTITY, lst.buy_day, lst.user_number,lst.user_id, rownum as rnum from( select b.buy_image, b.buy_product, b.buy_price, b.buy_number, b.BUY_QUANTITY,b.buy_day, u.user_number, u.user_id from buy_tb b, users_tb u, products_tb p where u.user_number= b.user_number and p.product_number = b.product_number and b.user_number = #{user_number}
and (buy_product Like '%'||#{keyword}||'%')
and (buy_day like '%'||#{keyword}||'%')
order by b.buy_number )lst ) WHERE RNUM BETWEEN #{start_row} and #{end_row}
