mapper.select:
SELECT * from(select lst.*, rownum as rnum from( select product_number AS productId, product_name AS productName, product_price AS productPrice, product_content AS productContent, product_image AS productImage, product_company AS productCompany, product_origin AS productOrigin FROM products_tb where product_status = 'Y'
and (product_name Like '%'||#{keyword}||'%')
ORDER BY product_name asc )lst ) WHERE RNUM BETWEEN #{start_row} and #{end_row}
