mapper.insert:
select seq_userboard_tb.nextval from dual
insert into userboard_tb( userboard_number, userboard_name, userboard_title, userboard_content, userboard_writedate, userboard_image, user_number
) values( #{userboard_number}, #{userboard_name}, #{userboard_title}, #{userboard_content}, sysdate, #{userboard_image}, #{user_number}
)

mapper.select:
select *
from(
select lst.*, rownum as rnum
from(
select
userboard_number,userboard_name,userboard_title,userboard_content,userboard_writedate,userboard_image,userboard_viewcnt,user_number
from userboard_tb
order by
userboard_number asc
)lst
)

select nvl(count(1),0) from
(select lst.*, rownum as rnum
from(
select
userboard_number,userboard_name,userboard_title,userboard_content,userboard_writedate,userboard_image,userboard_viewcnt,user_number
from userboard_tb
order by
userboard_number asc
)lst
)
